package pkg;

public class Swappingwithvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		int a=20,b=40,t;
		System.out.println("before swaping value of a="+a+"value of b="+b);
		
		t=a; //20
		a=b; //40
		b=t; //20
		System.out.println("After swaping value of a="+a+"value of b="+b);
		
		
		
	}

}
